import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';
import notepadImg from "../assets/notepad.png";
import food from "../assets/food.png";

const Main = () => {
  const [rows, setRows] = useState([]);
  const [newAmount, setNewAmount] = useState("");
  const [userInfo, setUserInfo] = useState({ username: "", calorie_norm: 0 });
  const [totalCalories, setTotalCalories] = useState(0);

  const token = localStorage.getItem("access_token");

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    localStorage.removeItem('isAuthenticated');
    window.location.href = '/login';
  };

  useEffect(() => {
    fetchUserData();
    fetchMeals();
  }, []);

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  const fetchUserData = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/api/user_info/", {
        headers: { Authorization: `Bearer ${token}` },
      });          
      setUserInfo(response.data);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:", error);
    }
  };

  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Å–ø–∏—Å–∫–∞ –µ–¥—ã
  const fetchMeals = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/api/meals/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRows(response.data);
      const total = response.data.reduce((acc, meal) => acc + meal.calories, 0);
      setTotalCalories(total);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Ä–∞—Ü–∏–æ–Ω–∞:", error);
    }
  };

  // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –µ–¥—ã
  const addRow = async () => {
    if (!newAmount.trim()) return;

    try {
      const response = await axios.post(
          "http://127.0.0.1:8000/api/meals/",
          JSON.stringify({ product: "Raw chicken", amount: Number(newAmount) }),
          {
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
          }
        );
      setRows([...rows, response.data]);
      setTotalCalories(prev => prev + response.data.calories);
      setNewAmount("");
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–∏ –µ–¥—ã:", error);
    }
  };

  const caloriePercentage = userInfo.calorie_norm
    ? Math.round((totalCalories / userInfo.calorie_norm) * 100)
    : 0;

  return (
    <div className="Mroot">
      <div className="Mleft_per_box">
        <p>Percentage of goal completed</p>
        <div className="Mper_circle">
          <h1 className="Mper_text_big">{caloriePercentage}%</h1>
          <p className="Mper_text">From {userInfo.calorie_norm}</p>
        </div>
        <p>Keep up the good work!üòÅ</p>

        <div className="Mbox_params">
          <div className="Mleaft_param_pox"><div>Calories:</div><div>{totalCalories}</div></div>
          <div className="Mleaft_param_pox"><div>Carbohydrates:</div><div>240 g</div></div>
          <div className="Mleaft_param_pox"><div>Proteins:</div><div>70 g</div></div>
          <div className="Mleaft_param_pox"><div>Fats:</div><div>101 g</div></div>
        </div>
      </div>

      <div className="Mright_big_box">
        <div className="Mheader">
          <div className="Mlogout_zone" onClick={handleLogout}>
            <div className="Mlogout_button">‚Ü™</div>
            <p className="Mlogout_text">Log out</p>
          </div>
          <h1>Welcome</h1>
          <Link className="Mprofile_zone" to="/profile">
            <div className="Mprofile_name">{userInfo.username}</div>
            <div className="Mprofile_button">üë§</div>
          </Link>
        </div>

        <div className="Mright_box">
          <div className="Mright_left_box">
            <div className="Mdate_n_search">
              <div className="Mdate">
                <div className="Mdate_button">‚îÉ‚óÄ</div>
                <p>Today</p>
                <div className="Mdate_button">‚ñ∂‚îÉ</div>
              </div>

              <div className="Msearch">
                <input className="Msearch_left" placeholder="Search food" />
                <div className="Msearch_right">üîçÔ∏é</div>
              </div>
            </div>

            <div className="Madd_food_box">
              <div className="Mfood_pic_big_box">
                <p className="Mfood_name">Raw chicken</p>
                <img className="Mpic_food" src={food} />
              </div>

              <div className="Madd_food_right_box">
                <p className="Mfood_right_box_text">Enter quantity:</p>
                <input
                  className="Menter_foob_gram"
                  placeholder="100 gramm"
                  value={newAmount}
                  onChange={(e) => setNewAmount(e.target.value)}
                />
                <div className="Madd_food_button" onClick={addRow}>Add</div>
              </div>
            </div>
          </div>

          <div className="Mright_right_box">
            <div className="notepad" style={{ backgroundImage: `url(${notepadImg})` }}>
              <h2 className="notebook-title">Already eaten</h2>
              <table className="data-table">
                <tbody className="Mtable_body">
                  {rows.map((row, index) => (
                    <tr key={index}>
                      <td className="Mtable_food_name">{row.product}</td>
                      <td className="Mtable_food_weight">{row.amount}g</td>
                      <td className="Mtable_food_rad"><input type="radio" /></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
