import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import axios from "axios";

const Profile = () => {
  const [userInfo, setUserInfo] = useState({
    username: "",
    height: "",
    weight: "",
    age: "",
    gender: "",
    calorie_norm: 0
  });

  const token = localStorage.getItem("access_token");

  // Получаем данные пользователя
  const fetchUserInfo = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:8000/api/user_info/", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUserInfo(res.data);
    } catch (err) {
      console.error("Ошибка загрузки данных пользователя:", err);
    }
  };

  useEffect(() => {
    fetchUserInfo();
  }, []);

  // Обновление локального состояния при изменении input
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo(prev => ({ ...prev, [name]: value }));
  };

  // Сохранение изменений на сервер
  const handleSave = async () => {
    try {
      await axios.patch(
        "http://127.0.0.1:8000/api/user_info/",
        userInfo,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Данные успешно сохранены!");
    } catch (err) {
      console.error("Ошибка сохранения данных:", err);
      alert("Не удалось сохранить данные");
    }
  };

  return (
    <div className="Lroot">
      <div className="Lleft_box">
        <Link to="/main" className="Lbutton_back">⬅️ Back</Link>
      </div>

      <div className="Lcenter_box">
        <p className="Lbig_login">Profile</p>
        <div className="Lcenter_box_child">
          <p className="Lenter_y_data">Edit your data:</p>
          <div className="Lcenter_inbox">

            <p className="Llogin_text">
              Name: 
              <input 
                type="text" 
                name="username" 
                value={userInfo.username} 
                onChange={handleChange} 
              />
            </p>

            <p className="Llogin_text">
              Height (cm): 
              <input 
                type="number" 
                name="height" 
                value={userInfo.height} 
                onChange={handleChange} 
              />
            </p>

            <p className="Llogin_text">
              Weight (kg): 
              <input 
                type="number" 
                name="weight" 
                value={userInfo.weight} 
                onChange={handleChange} 
              />
            </p>

            <p className="Llogin_text">
              Age: 
              <input 
                type="number" 
                name="age" 
                value={userInfo.age} 
                onChange={handleChange} 
              />
            </p>

            <p className="Llogin_text">
              Gender: 
              <select name="gender" value={userInfo.gender} onChange={handleChange}>
                <option value="">Select</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
              </select>
            </p>

            <p className="Llogin_text">
              Calorie norm: 
              <input 
                type="number" 
                name="calorie_norm" 
                value={userInfo.calorie_norm} 
                onChange={handleChange} 
              />
            </p>

            <button onClick={handleSave} className="Lsave_button">Save</button>

          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
