import React, { useState } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';

import notepadImg from "../assets/notepad.png";
import foodDefault from "../assets/notebook.png";

const API = "http://127.0.0.1:8000/api";

const Main = () => {
  const queryClient = useQueryClient();
  const token = localStorage.getItem("access_token");

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [newAmount, setNewAmount] = useState("");

  const [selectedFood, setSelectedFood] = useState({
    id: null,
    name: "",
    calories: 0,
    proteins: 0,
    fats: 0,
    carbohydrates: 0,
    photo: ""
  });

  // =============================
  // HELPERS
  // =============================
  const formatDate = (date) => date.toISOString().split('T')[0];

  const authHeader = {
    headers: { Authorization: `Bearer ${token}` }
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    window.location.href = '/login';
  };

  // =============================
  // QUERIES
  // =============================
  const { data: userInfo } = useQuery({
    queryKey: ['userInfo'],
    queryFn: async () => {
      const res = await axios.get(`${API}/user_info/`, authHeader);
      return res.data;
    }
  });

  const { data: meals = [], isLoading } = useQuery({
    queryKey: ['meals', formatDate(selectedDate)],
    queryFn: async () => {
      const res = await axios.get(
        `${API}/meals/?date=${formatDate(selectedDate)}`,
        authHeader
      );
      return res.data;
    }
  });

  // =============================
  // MUTATIONS
  // =============================
  const addMealMutation = useMutation({
    mutationFn: async () => {
      return axios.post(
        `${API}/meals/`,
        {
          food: selectedFood.id,
          amount: parseInt(newAmount),
          date: formatDate(selectedDate),
        },
        authHeader
      );
    },
    onSuccess: () => {
      queryClient.invalidateQueries(['meals']);
      setNewAmount("");
      setSearchQuery("");
      setSelectedFood({
        id: null,
        name: "",
        calories: 0,
        proteins: 0,
        fats: 0,
        carbohydrates: 0,
        photo: ""
      });
    }
  });

  const deleteMealMutation = useMutation({
    mutationFn: (id) =>
      axios.delete(`${API}/meals/${id}/`, authHeader),
    onSuccess: () => {
      queryClient.invalidateQueries(['meals']);
    }
  });

  // =============================
  // TOTALS
  // =============================
  const totalCalories = meals.reduce((s, m) => s + (m.calories || 0), 0);
  const totalProteins = meals.reduce((s, m) => s + (m.proteins || 0), 0);
  const totalFats = meals.reduce((s, m) => s + (m.fats || 0), 0);
  const totalCarbs = meals.reduce((s, m) => s + (m.carbohydrates || 0), 0);

  const caloriePercentage = userInfo?.calorie_norm
    ? Math.round((totalCalories / userInfo.calorie_norm) * 100)
    : 0;

  // =============================
  // SEARCH FOOD
  // =============================
  const handleSearchChange = async (e) => {
    const q = e.target.value;
    setSearchQuery(q);

    if (!q) return setSearchResults([]);

    const res = await axios.get(`${API}/foods/?q=${q}`, authHeader);
    setSearchResults(res.data);
  };

  const handleSelectFood = (food) => {
    setSelectedFood({ ...food, photo: food.photo || foodDefault });
    setSearchResults([]);
  };

  if (isLoading) return <div>–ó–∞–≥—Ä—É–∑–∫–∞...</div>;

  return (
    <div className="Mroot">
      {/* LEFT PANEL */}
      <div className="Mleft_per_box">
        <p>Percentage of goal completed</p>
        <div className="Mper_circle">
          <h1>{caloriePercentage}%</h1>
          <p>From {userInfo?.calorie_norm}</p>
        </div>

        <div className="Mbox_params">
          <div>Calories: {totalCalories}</div>
          <div>Proteins: {totalProteins}</div>
          <div>Fats: {totalFats}</div>
          <div>Carbs: {totalCarbs}</div>
        </div>
      </div>

      {/* RIGHT */}
      <div className="Mright_big_box">
        <div className="Mheader">
          <div onClick={handleLogout}>‚Ü™ Log out</div>
          <h1>Welcome</h1>
          <Link to="/profile">{userInfo?.username}</Link>
        </div>

        <input
          placeholder="Search food"
          value={searchQuery}
          onChange={handleSearchChange}
        />

        {searchResults.map(f => (
          <p key={f.id} onClick={() => handleSelectFood(f)}>
            {f.name}
          </p>
        ))}

        <input
          placeholder="grams"
          value={newAmount}
          onChange={(e) => setNewAmount(e.target.value)}
        />

        <button onClick={() => addMealMutation.mutate()}>
          Add
        </button>

        <table>
          <tbody>
            {meals.map(row => (
              <tr key={row.id}>
                <td>{row.food_name}</td>
                <td>{row.amount}g</td>
                <td>
                  <button onClick={() => deleteMealMutation.mutate(row.id)}>
                    üóëÔ∏è
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

      </div>
    </div>
  );
};

export default Main;
