import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import axios from "axios";

const Profile = () => {
  const [userInfo, setUserInfo] = useState({
    username: "",
    height: "",
    weight: "",
    age: "",
    gender: "",
    calorie_norm: 0
  });

  const token = localStorage.getItem("access_token");

  const fetchUserInfo = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:8000/api/user_info/", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUserInfo(res.data);
    } catch (err) {
      console.error("Ошибка загрузки данных пользователя:", err);
    }
  };

  useEffect(() => {
    fetchUserInfo();
  }, []);

  return (
    <div className="Lroot">
      <div className="Lleft_box">
        <Link to="/main" className="Lbutton_back">⬅️ Back</Link>
      </div>

      <div className="Lcenter_box">
        <p className="Lbig_login">Profile</p>
        <div className="Lcenter_box_child">
          <p className="Lenter_y_data">Your data:</p>
          <div className="Lcenter_inbox">
            <p className="Llogin_text"><b>Name:</b> {userInfo.username}</p>
            <p className="Llogin_text"><b>Height:</b> {userInfo.height} cm</p>
            <p className="Llogin_text"><b>Weight:</b> {userInfo.weight} kg</p>
            <p className="Llogin_text"><b>Age:</b> {userInfo.age}</p>
            <p className="Llogin_text"><b>Gender:</b> {userInfo.gender}</p>
            <p className="Llogin_text"><b>Calorie norm:</b> {userInfo.calorie_norm} kcal</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
