import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import axios from "axios";

const Profile = () => {
  const [profile, setProfile] = useState({
    username: "",
    height: "",
    weight: "",
    age: "",
    gender: "",
    calorie_norm: 0
  });

  const token = localStorage.getItem("access_token");

  const fetchProfile = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:8000/api/user_info/", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProfile(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  return (
    <div className="Lroot">
      <div className="Lleft_box">
        <Link to="/main" className="Lbutton_back">⬅️ Back</Link>
      </div>

      <div className="Lcenter_box">
        <p className="Lbig_login">Profile</p>
        <div className="Lcenter_box_child">
          <p className="Lenter_y_data">User data:</p>
          <div className="Lcenter_inbox">
            <p>Name: {profile.username}</p>
            <p>Height: {profile.height}</p>
            <p>Weight: {profile.weight}</p>
            <p>Age: {profile.age}</p>
            <p>Gender: {profile.gender}</p>
            <p>Calorie norm: {profile.calorie_norm}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
