import React, { useState, useEffect } from "react";
import axios from "axios";

const Main = () => {
  const [rows, setRows] = useState([]);
  const [newProduct, setNewProduct] = useState(""); // Теперь можно ввести название еды
  const [newAmount, setNewAmount] = useState("");
  const [userInfo, setUserInfo] = useState({ username: "", calorie_norm: 0 });
  const [totalCalories, setTotalCalories] = useState(0);

  const token = localStorage.getItem("access_token");

  useEffect(() => {
    fetchUserData();
    fetchMeals();
  }, []);

  const fetchUserData = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/api/user_info/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUserInfo(response.data);
    } catch (error) {
      console.error("Ошибка при получении данных пользователя:", error);
    }
  };

  const fetchMeals = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/api/meals/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRows(response.data);
      const total = response.data.reduce((acc, meal) => acc + meal.calories, 0);
      setTotalCalories(total);
    } catch (error) {
      console.error("Ошибка при загрузке рациона:", error);
    }
  };

  const addRow = async () => {
    // Проверка введённых данных
    if (!newProduct.trim()) return alert("Введите название продукта");
    if (!newAmount || isNaN(newAmount)) return alert("Введите корректное количество");

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/meals/",
        {
          product: newProduct.trim(),
          amount: Number(newAmount),
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      // Обновление таблицы и подсчёт калорий
      setRows([...rows, response.data]);
      setTotalCalories(prev => prev + response.data.calories);

      // Очистка полей ввода
      setNewProduct("");
      setNewAmount("");
    } catch (error) {
      console.error("Ошибка при добавлении еды:", error);
      if (error.response && error.response.data) {
        alert("Ошибка сервера: " + JSON.stringify(error.response.data));
      } else {
        alert("Ошибка сети или сервера");
      }
    }
  };

  // Остальная разметка остаётся такой же, только инпут для продукта:
  // <input value={newProduct} onChange={e => setNewProduct(e.target.value)} />
  // <input value={newAmount} onChange={e => setNewAmount(e.target.value)} />

  return (
    <div>
      {/* Пример полей ввода */}
      <input placeholder="Product" value={newProduct} onChange={e => setNewProduct(e.target.value)} />
      <input placeholder="Amount (grams)" value={newAmount} onChange={e => setNewAmount(e.target.value)} />
      <button onClick={addRow}>Add</button>
    </div>
  );
};

export default Main;
