import React, { useState } from "react";
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8000/api/api/token/", {
        username,
        password,
      });
      localStorage.setItem("access", response.data.access);
      localStorage.setItem("refresh", response.data.refresh);
      navigate("/main");
    } catch (err) {
      console.error(err);
      alert("Login failed");
    }
  };

  return (
    <div className="Lroot">
      <div className="Lleft_box">
        <Link to="/contact" className="Lbutton_back">⬅️ Back</Link>
      </div>

      <div className="Lcenter_box">
        <p className="Lbig_login">Log in</p>
        <div className="Lcenter_box_child">
          <p className="Lenter_y_data">Enter your data:</p>
          <div className="Lcenter_inbox">
            <form className="Lform" onSubmit={handleLogin}>
              <p className="Llogin_text">Name</p>
              <input
                placeholder="Enter name"
                className="Linput"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <p className="Llogin_text">Password</p>
              <input
                type="password"
                placeholder="Enter password"
                className="Linput"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <button type="submit" className="Lenter_button">➡️</button>
            </form>
          </div>
          <p>Forgot password?</p>
        </div>
      </div>

      <div className="Lright_box">
        <div className="Lenter__box">
          <p className="Llogin_text_bot">Log in</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
