import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';

const Profile = () => {
  const [userInfo, setUserInfo] = useState({
    username: "",
    height: "",
    weight: "",
    age: "",
    gender: "",
    calorie_norm: 0
  });

  const token = localStorage.getItem("access_token");

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    window.location.href = '/login';
  };

  const refreshToken = async () => {
    try {
      const refresh = localStorage.getItem("refresh_token");
      const response = await axios.post(
        "http://127.0.0.1:8000/api/api/token/refresh/",
        { refresh }
      );
      localStorage.setItem("access_token", response.data.access);
      return response.data.access;
    } catch {
      handleLogout();
    }
  };

  const fetchUserInfo = async () => {
    try {
      let access = token;
      const res = await axios.get("http://127.0.0.1:8000/api/user_info/", {
        headers: { Authorization: `Bearer ${access}` }
      });
      setUserInfo(res.data);
    } catch (err) {
      if (err.response?.status === 401) {
        const newAccess = await refreshToken();
        if (newAccess) {
          const res = await axios.get("http://127.0.0.1:8000/api/user_info/", {
            headers: { Authorization: `Bearer ${newAccess}` }
          });
          setUserInfo(res.data);
        }
      } else {
        console.error(err);
      }
    }
  };

  useEffect(() => {
    fetchUserInfo();
  }, []);

  return (
    <div className="Lroot">
      <div className="Lleft_box">
        <Link to="/main" className="Lbutton_back">⬅️ Back</Link>
        <button onClick={handleLogout} className="Llogout_button">Log out</button>
      </div>

      <div className="Lcenter_box">
        <p className="Lbig_login">Profile</p>
        <div className="Lcenter_box_child">
          <p className="Lenter_y_data">Your data:</p>
          <div className="Lcenter_inbox">
            <p className="Llogin_text">Username: {userInfo.username}</p>
            <p className="Llogin_text">Height: {userInfo.height} cm</p>
            <p className="Llogin_text">Weight: {userInfo.weight} kg</p>
            <p className="Llogin_text">Age: {userInfo.age}</p>
            <p className="Llogin_text">Gender: {userInfo.gender}</p>
            <p className="Llogin_text">Calorie Norm: {userInfo.calorie_norm} kcal</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
